<?$my_dir = fetch_assoc(query("SELECT * FROM `obmennik_dir` WHERE `my` = '1' LIMIT 1"));$k_p=result(query("SELECT COUNT(*) FROM `obmennik_files` WHERE `id_dir` != '$my_dir[id]'",$db), 0);$k_n= result(query("SELECT COUNT(*) FROM `obmennik_files` WHERE `id_dir` != '$my_dir[id]' AND `time_go` > '".$ftime."'",$db), 0);if ($k_n==0)$k_n=NULL;else $k_n='+'.$k_n;echo "($k_p) <font color='red'>$k_n</font>";?>